script:
//created by enggar 11 September 2019
//remark bom hilang setelah input
frappe.ui.form.on("Work Order","bom_no", function(frm){
if(cur_frm.doc.bom_no != " "){
cur_frm.doc.bom = "";
cur_frm.refresh_field('bom');
}
});

//created by enggar 13 September 2019
//kalkulasi total inject & shot
frappe.ui.form.on("Work Order","uom", function(frm){
cur_frm.doc.total_inject += cur_frm.doc.qty / cur_frm.doc.uom;
cur_frm.doc.shot = cur_frm.doc.total_inject;
cur_frm.refresh_field('shot');
cur_frm.refresh_field('total_inject');
});

//kalkulasi total cycle time gross & netto, target production qty
frappe.ui.form.on("Work Order","operational_time", function(frm){
cur_frm.doc.cycle_time_gross += (3600*flt(cur_frm.doc.operational_time + cur_frm.doc.loss_time))/flt(cur_frm.doc.shot);
cur_frm.doc.cycle_time_netto += (3600*flt(cur_frm.doc.operational_time)/cur_frm.doc.shot);
cur_frm.doc.target_production_qty += (3600*flt(cur_frm.doc.operational_time + cur_frm.doc.loss_time))*flt(cur_frm.doc.uom)/flt(cur_frm.doc.cycle_time_netto);
cur_frm.refresh_field('target_production_qty');
cur_frm.refresh_field('cycle_time_gross');
cur_frm.refresh_field('cycle_time_netto');
});

//kalkulasi PPM
frappe.ui.form.on("Work Order","ng", function(frm){
cur_frm.doc.ppm += (cur_frm.doc.ng / cur_frm.doc.qty)*1000000;
cur_frm.refresh_field('ppm');
});

//creator
frappe.ui.form.on("Work Order", "validate", function(frm) {
    cur_frm.set_value("creator", frm.doc.owner);
});

//edited by enggar 27 September 2019
//NG Weight
frappe.ui.form.on("Work Order","standar_weight", function(frm){
cur_frm.doc.ng_weight+= (cur_frm.doc.standar_weight * cur_frm.doc.ng);
cur_frm.refresh_field('ng_weight');
});

