    cur_frm.set_value("creator", frm.doc.owner);
});

frappe.ui.form.on("Stock Entry", {
	"purpose": function(frm) {
		if (frm.doc.purpose=="Material Receipt" || frm.doc.purpose == "Material Issue")) {
			frm.set_df_property("bc_type", "options", 
				[
					"",
					"BC 2.3",
					"BC 2.6.2",
					"BC 2.7",
					"BC 4.0",
					"PPFTZ02"
				]
			);
		}
		else {
			frm.set_df_property("bc_type", "options", [ "" ]);
		}
	}
});

frappe.ui.form.on("Stock Entry", {
	"bc_type": function(frm) {
		if (frm.doc.bc_type==="") {
			frm.set_df_property("bc_type", "reqd", 0);
			frm.set_df_property("bc_number", "reqd", 0);
			frm.set_df_property("bc_date", "reqd", 0);
			frm.set_df_property("supplier_bc", "reqd", 0);
		}
		else {
			frm.set_df_property("bc_type", "reqd", 1);
			frm.set_df_property("bc_number", "reqd", 1);
			frm.set_df_property("bc_date", "reqd", 1);
			frm.set_df_property("supplier_bc", "reqd", 1);
		}
	}
});
